INSERT INTO [dbo].[Articles] ([Id], [Abstract], [AddedBy], [AddedDate], [Approved], [Body], [CategoryId], [City], [CommentsEnabled], [Country], [ExpireDate], [Listed], [OnlyForMembers], [ReleaseDate], [State], [Title], [TotalRating], [ViewCount], [Votes]) VALUES (N'c873dda0-8e53-448f-caa8-08d5eae4ce9b', N'Scientists have stumbled across a huge group of previously unknown Ad?lie penguins on the most northerly point of the Antarctic Peninsula.', N'Anonymus', N'2005-09-03 12:14:00', 1, N'Numbering more than 1.5 million birds, they were first noticed when great patches of their poo, or guano, showed up in pictures taken from space.The animals are crammed on to a rocky archipelago called the Danger Islands.The researchers, who detail the discovery in the journal Scientific Reports, say it is a total surprise."It''s a classic case of finding something where no-one really looked! The Danger Islands are hard to reach, so people didn''t really try that hard," team-member Dr Tom Hart from Oxford University, UK, told BBC News.', N'6b9ffd1e-d6ef-46f0-95a0-08d5eae4ce99', N'Lviv', 1, N'Ukraine', N'2025-10-05 12:15:00', 1, 0, N'2005-09-03 12:15:00', NULL, N'Penguin super-colony spotted from space', 3, 8, 1)
INSERT INTO [dbo].[Articles] ([Id], [Abstract], [AddedBy], [AddedDate], [Approved], [Body], [CategoryId], [City], [CommentsEnabled], [Country], [ExpireDate], [Listed], [OnlyForMembers], [ReleaseDate], [State], [Title], [TotalRating], [ViewCount], [Votes]) VALUES (N'89291011-4939-4125-caa9-08d5eae4ce9b', N'Water firms have been accused of exploiting their monopoly power and neglecting the environment.', N'Vasiliy', N'2005-09-03 12:14:00', 1, N'The critcism comes from Environment Secretary Michael Gove, who said some firms had paid no tax and hidden their earnings offshore.And he promised to back the regulator Ofwat in tightening up rules to protect bill-payers and the environment.The industry body Water UK has yet to respond.But Mr Gove’s speech to a water industry conference must have made uncomfortable listening.He complained that water firms had paid out almost all of their ?18bn profits to shareholders, instead of re-investing it in mending leaks and protecting the environment.And he named names: The boss of United Utilities taking home ?2.8m a year; the head of Severn Trent, ?2.4m.The secretary of state said last year Anglian and Southern paid zero corporation tax, whilst Thames has paid no corporation tax for a decade.He suggested: "Some of their best brains appear to be as intent on financial engineering as much as real engineering."', N'6b9ffd1e-d6ef-46f0-95a0-08d5eae4ce99', N'Lviv', 1, N'Ukraine', N'2025-10-05 12:15:00', 1, 0, N'2005-09-03 12:15:00', NULL, N'Gove lambasts water company chiefs', 3, 8, 1)
INSERT INTO [dbo].[Articles] ([Id], [Abstract], [AddedBy], [AddedDate], [Approved], [Body], [CategoryId], [City], [CommentsEnabled], [Country], [ExpireDate], [Listed], [OnlyForMembers], [ReleaseDate], [State], [Title], [TotalRating], [ViewCount], [Votes]) VALUES (N'5039730b-9113-49b5-caaa-08d5eae4ce9b', N'Cryptocurrencies such as Bitcoin should be regulated to crack down on illegal activities and protect the financial system, Mark Carney has warned....', N'Ethan', N'2005-09-03 12:14:00', 1, N'"The time has come to hold the crypto-asset ecosystem to the same standards as the rest of the financial system," Mr Carney said in a speech on Friday.Cryptocurrencies do not yet pose risks to financial stability, he said.But he said that could change if more people began investing in them. Although some countries have banned them, Mr Carney said regulation would be a better approach."A better path would be to regulate elements of the rypto-asset ecosystem to combat illicit activities, promote market integrity, and protect the safety and soundness of the financial system," he said.', N'e365cbb4-ca90-4d81-95a1-08d5eae4ce99', N'Lviv', 1, N'Ukraine', N'2025-10-05 12:15:00', 1, 0, N'2005-09-03 12:15:00', NULL, N'Carney calls for crackdown on cryptocurrency ''mania''', 3, 8, 1)
INSERT INTO [dbo].[Articles] ([Id], [Abstract], [AddedBy], [AddedDate], [Approved], [Body], [CategoryId], [City], [CommentsEnabled], [Country], [ExpireDate], [Listed], [OnlyForMembers], [ReleaseDate], [State], [Title], [TotalRating], [ViewCount], [Votes]) VALUES (N'f5c45ccc-9db5-4395-caab-08d5eae4ce9b', N'The main trading partners of the US have reacted angrily after President Donald Trump announced plans to impose tariffs on steel and aluminium imports.', N'Anonymus', N'2005-09-03 12:14:00', 1, N'Canada and the EU said they would bring forward their own countermeasures to the steep new tariffs.Mexico, China and Brazil have also said they are weighing up retaliatory steps.Mr Trump tweeted that the US had been "decimated by unfair trade and bad policy". He said steel imports would face a 25% tariff and aluminium 10%.However, critics argue that the tariffs would fail to protect American jobs and would ultimately put up prices for consumers.The news sent shares in Asia down on Friday, with Japan''s benchmark Nikkei 225 losing more than 2% by mid-morning.', N'e365cbb4-ca90-4d81-95a1-08d5eae4ce99', N'Lviv', 1, N'Ukraine', N'2025-10-05 12:15:00', 1, 0, N'2005-09-03 12:15:00', NULL, N'Trump steel tariffs: Trading partners threaten retaliation', 3, 8, 1)
INSERT INTO [dbo].[Articles] ([Id], [Abstract], [AddedBy], [AddedDate], [Approved], [Body], [CategoryId], [City], [CommentsEnabled], [Country], [ExpireDate], [Listed], [OnlyForMembers], [ReleaseDate], [State], [Title], [TotalRating], [ViewCount], [Votes]) VALUES (N'fb52424c-63ca-4dc1-caac-08d5eae4ce9b', N'President Trump''s backing for slapping tariffs on imports of washing machines and solar panels will hit China and South Korea hardest.', N'Vasiliy', N'2005-09-03 12:14:00', 1, N'And it has opened up the prospect of some retaliation - especially from Beijing.The hardline Chinese publication Global Times says "nothing good" would come out of a trade war with President Trump, and has warned that China could fight back.There''s lots at stake. The two countries did $578.6bn worth of trade in 2016.And by the US government''s own estimates that trade supports just under a million American jobs.', N'e365cbb4-ca90-4d81-95a1-08d5eae4ce99', N'Lviv', 1, N'Ukraine', N'2025-10-05 12:15:00', 1, 0, N'2005-09-03 12:15:00', NULL, N'What could China do in a US trade war?', 3, 8, 1)
