INSERT INTO [dbo].[Articles] ([Id], [Abstract], [AddedBy], [AddedDate], [Approved], [Body], [CategoryId], [City], [CommentsEnabled], [Country], [ExpireDate], [Listed], [OnlyForMembers], [ReleaseDate], [State], [Title], [TotalRating], [ViewCount], [Votes]) VALUES (N'a9386472-4277-4086-b796-08d5eaf4b448', N'Scientists have stumbled across a huge group of previously unknown Ad?lie penguins on the most northerly point of the Antarctic Peninsula.', N'Anonymus', N'2005-09-03 12:14:00', 1, N'Numbering more than 1.5 million birds, they were first noticed when great patches of their poo, or guano, showed up in pictures taken from space.The animals are crammed on to a rocky archipelago called the Danger Islands.The researchers, who detail the discovery in the journal Scientific Reports, say it is a total surprise."It''s a classic case of finding something where no-one really looked! The Danger Islands are hard to reach, so people didn''t really try that hard," team-member Dr Tom Hart from Oxford University, UK, told BBC News.', N'6aa44ec8-92e1-4663-a770-08d5eaf4b446', N'Lviv', 1, N'Ukraine', N'2025-10-05 12:15:00', 1, 0, N'2005-09-03 12:15:00', NULL, N'Penguin super-colony spotted from space', 3, 8, 1)
INSERT INTO [dbo].[Articles] ([Id], [Abstract], [AddedBy], [AddedDate], [Approved], [Body], [CategoryId], [City], [CommentsEnabled], [Country], [ExpireDate], [Listed], [OnlyForMembers], [ReleaseDate], [State], [Title], [TotalRating], [ViewCount], [Votes]) VALUES (N'2e8882f0-c0eb-496c-b797-08d5eaf4b448', N'Water firms have been accused of exploiting their monopoly power and neglecting the environment.', N'Vasiliy', N'2005-09-03 12:14:00', 1, N'The critcism comes from Environment Secretary Michael Gove, who said some firms had paid no tax and hidden their earnings offshore.And he promised to back the regulator Ofwat in tightening up rules to protect bill-payers and the environment.The industry body Water UK has yet to respond.But Mr Gove’s speech to a water industry conference must have made uncomfortable listening.He complained that water firms had paid out almost all of their ?18bn profits to shareholders, instead of re-investing it in mending leaks and protecting the environment.And he named names: The boss of United Utilities taking home ?2.8m a year; the head of Severn Trent, ?2.4m.The secretary of state said last year Anglian and Southern paid zero corporation tax, whilst Thames has paid no corporation tax for a decade.He suggested: "Some of their best brains appear to be as intent on financial engineering as much as real engineering."', N'6aa44ec8-92e1-4663-a770-08d5eaf4b446', N'Lviv', 1, N'Ukraine', N'2025-10-05 12:15:00', 1, 0, N'2005-09-03 12:15:00', NULL, N'Gove lambasts water company chiefs', 3, 8, 1)
INSERT INTO [dbo].[Articles] ([Id], [Abstract], [AddedBy], [AddedDate], [Approved], [Body], [CategoryId], [City], [CommentsEnabled], [Country], [ExpireDate], [Listed], [OnlyForMembers], [ReleaseDate], [State], [Title], [TotalRating], [ViewCount], [Votes]) VALUES (N'6704bb3b-778e-491b-b798-08d5eaf4b448', N'Cryptocurrencies such as Bitcoin should be regulated to crack down on illegal activities and protect the financial system, Mark Carney has warned....', N'Ethan', N'2005-09-03 12:14:00', 1, N'"The time has come to hold the crypto-asset ecosystem to the same standards as the rest of the financial system," Mr Carney said in a speech on Friday.Cryptocurrencies do not yet pose risks to financial stability, he said.But he said that could change if more people began investing in them. Although some countries have banned them, Mr Carney said regulation would be a better approach."A better path would be to regulate elements of the rypto-asset ecosystem to combat illicit activities, promote market integrity, and protect the safety and soundness of the financial system," he said.', N'34415587-bff1-4993-a771-08d5eaf4b446', N'Lviv', 1, N'Ukraine', N'2025-10-05 12:15:00', 1, 0, N'2005-09-03 12:15:00', NULL, N'Carney calls for crackdown on cryptocurrency ''mania''', 3, 8, 1)
