INSERT INTO [dbo].[Articles] ([Id], [Abstract], [AddedBy], [AddedDate], [Approved], [Body], [CategoryId], [City], [CommentsEnabled], [Country], [ExpireDate], [Listed], [OnlyForMembers], [ReleaseDate], [State], [Title], [TotalRating], [ViewCount], [Votes]) VALUES (N'59c096a1-c543-43cf-afe6-08d5ebded72b', N'The main trading partners of the US have reacted angrily after President Donald Trump announced plans to impose tariffs on steel and aluminium imports.', N'Anonymus', N'2005-09-03 12:14:00', 1, N'Canada and the EU said they would bring forward their own countermeasures to the steep new tariffs.Mexico, China and Brazil have also said they are weighing up retaliatory steps.Mr Trump tweeted that the US had been "decimated by unfair trade and bad policy". He said steel imports would face a 25% tariff and aluminium 10%.However, critics argue that the tariffs would fail to protect American jobs and would ultimately put up prices for consumers.The news sent shares in Asia down on Friday, with Japan''s benchmark Nikkei 225 losing more than 2% by mid-morning.', N'39ec9565-2258-49f3-cee2-08d5ebded729', N'Lviv', 1, N'Ukraine', N'2025-10-05 12:15:00', 1, 0, N'2005-09-03 12:15:00', NULL, N'Trump steel tariffs: Trading partners threaten retaliation', 3, 8, 1)
