INSERT INTO [dbo].[Articles] ([Id], [Abstract], [AddedBy], [AddedDate], [Approved], [Body], [CategoryId], [City], [CommentsEnabled], [Country], [ExpireDate], [Listed], [OnlyForMembers], [ReleaseDate], [State], [Title], [TotalRating], [ViewCount], [Votes]) VALUES (N'f4d4dc4a-a9a6-4ee2-1d09-08d5ebe1146b', N'The main trading partners of the US have reacted angrily after President Donald Trump announced plans to impose tariffs on steel and aluminium imports.', N'Anonymus', N'2005-09-03 12:14:00', 1, N'Canada and the EU said they would bring forward their own countermeasures to the steep new tariffs.Mexico, China and Brazil have also said they are weighing up retaliatory steps.Mr Trump tweeted that the US had been "decimated by unfair trade and bad policy". He said steel imports would face a 25% tariff and aluminium 10%.However, critics argue that the tariffs would fail to protect American jobs and would ultimately put up prices for consumers.The news sent shares in Asia down on Friday, with Japan''s benchmark Nikkei 225 losing more than 2% by mid-morning.', N'1abf66f7-b48c-41c6-37d5-08d5ebe11469', N'Lviv', 1, N'Ukraine', N'2025-10-05 12:15:00', 1, 0, N'2005-09-03 12:15:00', NULL, N'Trump steel tariffs: Trading partners threaten retaliation', 3, 8, 1)
INSERT INTO [dbo].[Articles] ([Id], [Abstract], [AddedBy], [AddedDate], [Approved], [Body], [CategoryId], [City], [CommentsEnabled], [Country], [ExpireDate], [Listed], [OnlyForMembers], [ReleaseDate], [State], [Title], [TotalRating], [ViewCount], [Votes]) VALUES (N'01fff3a8-0c49-41b4-1d0a-08d5ebe1146b', N'President Trump''s backing for slapping tariffs on imports of washing machines and solar panels will hit China and South Korea hardest.', N'Vasiliy', N'2005-09-03 12:14:00', 1, N'And it has opened up the prospect of some retaliation - especially from Beijing.The hardline Chinese publication Global Times says "nothing good" would come out of a trade war with President Trump, and has warned that China could fight back.There''s lots at stake. The two countries did $578.6bn worth of trade in 2016.And by the US government''s own estimates that trade supports just under a million American jobs.', N'1abf66f7-b48c-41c6-37d5-08d5ebe11469', N'Lviv', 1, N'Ukraine', N'2025-10-05 12:15:00', 1, 0, N'2005-09-03 12:15:00', NULL, N'What could China do in a US trade war?', 3, 8, 1)
