INSERT INTO [dbo].[Articles] ([Id], [Abstract], [AddedBy], [AddedDate], [Approved], [Body], [CategoryId], [City], [CommentsEnabled], [Country], [ExpireDate], [Listed], [OnlyForMembers], [ReleaseDate], [State], [Title], [TotalRating], [ViewCount], [Votes]) VALUES (N'46b40971-18d5-4bc9-baec-08d5ebe3aeb5', N'Scientists have stumbled across a huge group of previously unknown Ad?lie penguins on the most northerly point of the Antarctic Peninsula.', N'Anonymus', N'2005-09-03 12:14:00', 1, N'Numbering more than 1.5 million birds, they were first noticed when great patches of their poo, or guano, showed up in pictures taken from space.The animals are crammed on to a rocky archipelago called the Danger Islands.The researchers, who detail the discovery in the journal Scientific Reports, say it is a total surprise."It''s a classic case of finding something where no-one really looked! The Danger Islands are hard to reach, so people didn''t really try that hard," team-member Dr Tom Hart from Oxford University, UK, told BBC News.', N'ea84cf4a-bbd1-45de-ec9b-08d5ebe3aeb3', N'Lviv', 1, N'Ukraine', N'2025-10-05 12:15:00', 1, 0, N'2005-09-03 12:15:00', NULL, N'Penguin super-colony spotted from space', 3, 8, 1)
